import React, { useState, useRef, useCallback } from 'react'
import TitleGridItem from './Titles/TitleGridItem'
import { Row, Alert, Spinner } from 'react-bootstrap'
import axios from 'axios'
import { useAxiosGet } from '../Hooks/HttpsRequests'


export default function Movies() {
    const [pageNumber, setPageNumber] = useState(1)

    const url = "https://api.themoviedb.org/3/trending/movie/day?api_key=1e732b8b29336f4c9263a24d54a86994"
    let movies = useAxiosGet(url, pageNumber)

    let content = null;

    const observer = useRef()
    const lastMovieElementRef = useCallback(
        (node) => {            
            if (movies.loading) return false
            if (observer.current) observer.current.disconnect()
            observer.current = new IntersectionObserver(entries => {
                if (entries[0].isIntersecting && movies.hasMore) {
                    setPageNumber(prevPageNumber => prevPageNumber + 1)
                }
            })
            if (node) observer.current.observe(node)
        },
        [movies.loading, movies.hasMore],
    )
    if (movies.error) {
        content = <div>
            <Alert key={1} variant='danger'>There was an error! :(</Alert>
        </div>
    }
    if (movies.loading) {
        content = <div>
            <Spinner animation="grow" variant="info" />
        </div>
    }
    if (movies.data) {
        let data = Array.from(movies.data.results);
        console.log(data)
        content = data.map((movie, index) => {
            if (data.length === index + 1) return <div key={movie.id} ref={lastMovieElementRef}><TitleGridItem movie={movie} /></div>
            else return <div key={movie.id}><TitleGridItem movie={movie} /></div>
        }

        )
    }

    return (
        <div>
            <Row>
                {content}
            </Row>
        </div>
    )
}
