import axios from 'axios'
import React, {useState, useEffect} from 'react'

export function useAxiosGet(url,pageNumber) {
    const [request, setRequest] = useState({
        loading:false,
        data:null,
        hasMore:false,
        error:false
    })

    useEffect(() => {
        setRequest({
            loading:true,
            data:null,
            error:false
        })
        axios(url + "&page=" + pageNumber)
            .then(response => {
                setRequest({
                    loading:false,
                    data:response.data.results,
                    hasMore:response.data.page < response.data.total_pages,
                    error:false
                })
                console.log("has more?", response.data.page, response.data.total_pages)
            })
            .catch(() => {
                setRequest({
                    loading:false,
                    data:null,
                    error:true
                })
            })
    },[url])
    return request;
}
