import React from 'react'
import TitleGridItem from './Titles/TitleGridItem'
import {Row,Alert, Spinner} from 'react-bootstrap'
import axios from 'axios'
import { useAxiosGet } from '../Hooks/HttpsRequests'


export default function Movies() {
    const url = "https://api.themoviedb.org/3/trending/all/day?api_key=1e732b8b29336f4c9263a24d54a86994"
    let movies = useAxiosGet(url)

    let content = null;

    if(movies.error){
        content = <div>
            <Alert key={1} variant='danger'>There was an error! :(</Alert>
        </div>
    }
    if(movies.loading){
        content = <div>
            <Spinner animation="grow" variant="info" />
        </div>
    }
    if(movies.data){
        content = movies.data.map((movie) => <div key={movie.id}>
                <TitleGridItem movie={movie}/>
        </div>)
    }

    return (
        <div>
            <h1>Movies</h1>
            <Row>
                <TitleGridItem image="https://i.pinimg.com/originals/57/13/c8/5713c8fe95459ee7230191c0fb399afc.jpg" title="Run Forrest, Run!" genre="Drama"/>
            </Row>

        </div>
    )
}
